import React, { useState } from "react";
import axios from "axios";

const PodcastDetails = ({ item }) => {
  if (!item) return null;
  const { title, description, image: imageUrl } = item;
  return (
    <div className="podcast-summary">
      <div className="podcast-image">
        <img src={imageUrl} alt="Podcast Cover" />
      </div>
      <div className="podcast-details">
        <h2 className="podcast-title">{title}</h2>
        <p className="podcast-description">{description}</p>
      </div>
    </div>
  );
};

const PodcastSummary = ({ summary }) => {
  if (!summary) return null;
  return (
    <>
      <p className="h3">Podcast summary: </p>
      <p>{summary}</p>
    </>
  );
};

function App() {
  const [rss, setRss] = useState("");
  const [result, setResult] = useState({});
  const [loading, setLoading] = useState(false);

  const handleRssSubmit = async (e) => {
    setLoading(true);
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/transcribe/", {
        rss,
      });
      setResult(response);
      setLoading(false);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="container mt-5 d-flex justify-content-center align-self-center">
      {loading && <h1>Loading...</h1>}
      <form onSubmit={handleRssSubmit}>
        <label>
          RSS Feed URL:
          <input
            type="text"
            className="form-control"
            value={rss}
            onChange={(e) => setRss(e.target.value)}
          />
        </label>
        <button type="submit" className="btn btn-secondary btn-sm mx-2">
          Transcribe
        </button>
      </form>
      <PodcastDetails {...result} />
      <PodcastSummary {...result} />
    </div>
  );
}

export default App;
