import React, { useState, useRef, useMemo } from "react";
import axios from "axios";
import "./App.css";

const fixture = {
  summary:
    "Susan B. Anthony's speech was delivered in 1873, during her defense in court after being arrested for casting an illegal vote in the 1872 presidential election. In her speech, Anthony argues that her action was not a crime but rather an exercise of her rights as a United States citizen, as guaranteed by the Constitution. She emphasizes that the Constitution was formed by \"we the people,\" which includes women, and that denying women the right to vote goes against the principles of democracy and liberty. Anthony argues that any state law that discriminates against women's voting rights is null and void, just like laws discriminating against African Americans.",
  item: {
    url: "https://pdcn.co/e/www.buzzsprout.com/1411957/13404726-lola-habashaa-fi-bilxiginnaa.mp3",
    duration: "5949",
    title: "Lola Habashaa Fi Bilxiginnaa",
    description:
      '<a href="https://www.paypal.com/mep/dashboard" rel="payment">Support the show</a>',
    author: "CHALA ABATE",
    date: "Mon, 14 Aug 2023 21:00:00 +0200",
    type: {
      itunes_title: "Lola Habashaa Fi Bilxiginnaa",
      title: "Lola Habashaa Fi Bilxiginnaa",
      title_detail: {
        type: "text/plain",
        language: null,
        base: "https://feeds.buzzsprout.com/1411957.rss",
        value: "Lola Habashaa Fi Bilxiginnaa",
      },
      summary:
        '<a href="https://www.paypal.com/mep/dashboard" rel="payment">Support the show</a>',
      summary_detail: {
        type: "text/html",
        language: null,
        base: "https://feeds.buzzsprout.com/1411957.rss",
        value:
          '<a href="https://www.paypal.com/mep/dashboard" rel="payment">Support the show</a>',
      },
      content: [
        {
          type: "text/html",
          language: null,
          base: "https://feeds.buzzsprout.com/1411957.rss",
          value:
            '<a href="https://www.paypal.com/mep/dashboard" rel="payment">Support the show</a>',
        },
      ],
      image: {
        href: "https://storage.buzzsprout.com/variants/cmjdz6hyhchdhrqckwo1kfzhpq9f/2c6fbec80c7e8317ed20281c1521e888d2fe1922f26eb50d7efad8f0529ebc71.jpeg",
      },
      authors: [
        {
          name: "CHALA ABATE",
        },
      ],
      author: "CHALA ABATE",
      author_detail: {
        name: "CHALA ABATE",
      },
      links: [
        {
          length: "71439823",
          type: "audio/mpeg",
          href: "https://pdcn.co/e/www.buzzsprout.com/1411957/13404726-lola-habashaa-fi-bilxiginnaa.mp3",
          rel: "enclosure",
        },
      ],
      id: "Buzzsprout-13404726",
      guidislink: false,
      published: "Mon, 14 Aug 2023 21:00:00 +0200",
      published_parsed: [2023, 8, 14, 19, 0, 0, 0, 226, 0],
      itunes_duration: "5949",
      itunes_episodetype: "full",
      itunes_explicit: null,
    },
    image:
      "https://storage.buzzsprout.com/variants/cmjdz6hyhchdhrqckwo1kfzhpq9f/2c6fbec80c7e8317ed20281c1521e888d2fe1922f26eb50d7efad8f0529ebc71.jpeg",
  },
};
const PodcastDetails = ({ item }) => {
  const published = useMemo(() => {
    const d = new Date(date);
    return d.toLocaleDateString();
  });
  if (!item) return null;
  const { title, description, image: imageUrl, date } = item;
  return (
    <div className="podcast-summary">
      <div className="podcast-image">
        <img src={imageUrl} alt="Podcast Cover" />
      </div>
      <div className="podcast-details">
        <h2 className="podcast-title">{title}</h2>
        <small>{published}</small>
        <p className="podcast-description">{description}</p>
      </div>
    </div>
  );
};

const PodcastSummary = ({ summary }) => {
  if (!summary) return null;
  return (
    <>
      <p className="h3">Podcast summary: </p>
      <p>{summary}</p>
    </>
  );
};

function ButtonSubmit() {
  return (
    <button
      type="submit"
      className="btn btn-primary d-flex align-items-center align-self-end"
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="16"
        height="16"
        fill="currentColor"
        class="bi bi-rss"
        viewBox="0 0 16 16"
      >
        <path d="M14 1a1 1 0 0 1 1 1v12a1 1 0 0 1-1 1H2a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1h12zM2 0a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z" />
        <path d="M5.5 12a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm-3-8.5a1 1 0 0 1 1-1c5.523 0 10 4.477 10 10a1 1 0 1 1-2 0 8 8 0 0 0-8-8 1 1 0 0 1-1-1zm0 4a1 1 0 0 1 1-1 6 6 0 0 1 6 6 1 1 0 1 1-2 0 4 4 0 0 0-4-4 1 1 0 0 1-1-1z" />
      </svg>
      <span className="mx-2 ">Submit</span>
    </button>
  );
}

const Form = React.forwardRef(({ submit, setRSS }, ref) => {
  return (
    <form onSubmit={submit}>
      <label>
        <span>Enter RSS Feed URL...</span>
        <input
          ref={ref}
          type="text"
          className="form-control my-2"
          onChange={(e) => setRSS(e.target.value)}
        />
      </label>
      <ButtonSubmit />
    </form>
  );
});

const Loading = ({ loading }) => loading && <h1>Generating...</h1>;

function App() {
  const ref = useRef();
  const [rss, setRSS] = useState("");
  const [result, setResult] = useState(fixture);
  const [loading, setLoading] = useState(false);

  const handleRssSubmit = async (e) => {
    setLoading(true);
    setResult(null);
    e.preventDefault();
    try {
      const response = await axios.post("transcribe/", {
        rss,
      });
      setResult(response.data);
      setLoading(false);
      ref.current.value = "";
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="mt-5 App-container">
      <Form ref={ref} submit={handleRssSubmit} setRSS={setRSS} />
      <Loading {...loading} />
      <PodcastDetails {...result} />
      <PodcastSummary {...result} />
    </div>
  );
}

export default App;
